package sec06;

public class BitLogExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("45 & 24 = " + (45 & 25)); //비트연산에선 1개만 써준다.
		System.out.println("45 | 25 = " + (45 | 25));
		System.out.println("45 ^ 25 = " + (45 ^ 25));
		System.out.println("~45 = " + (~45));
	}

}
